import 'package:flutter/material.dart';
import 'package:flutter_svg/flutter_svg.dart';

class ActionButtons extends StatelessWidget {
  const ActionButtons({
    super.key,
    required this.onSkip,
    required this.onLike,
    required this.onSuperLike,
  });

  final VoidCallback onSkip;
  final VoidCallback onLike;
  final VoidCallback onSuperLike;

  @override
  Widget build(BuildContext context) {
    const double bigSize = 66;
    const double medSize = 60;
    return Container(
      padding: const EdgeInsets.symmetric(horizontal: 16, vertical: 14),
      child: Row(
        mainAxisAlignment: MainAxisAlignment.spaceEvenly,
        children: [
          _ActionCircle(
            size: medSize,
            tooltip: 'Boost',
            onTap: () {},
            child: SvgPicture.string(_boostSvg),
          ),
          _ActionCircle(
            size: bigSize,
            tooltip: 'Pass',
            onTap: onSkip,
            child: SvgPicture.string(_closeSvg, width: 32, height: 32),
          ),
          _ActionCircle(
            size: bigSize,
            tooltip: 'Love',
            onTap: onLike,
            child: SvgPicture.string(_likeSvg, width: 32, height: 32),
          ),
          _ActionCircle(
            size: medSize,
            tooltip: 'Super Love',
            onTap: onSuperLike,
            child: SvgPicture.string(_superLikeSvg, width: 30, height: 30),
          ),
          _ActionCircle(
            size: medSize,
            tooltip: 'DM',
            onTap: () {},
            child: Transform.rotate(
              angle: -20 * 3.1415926535 / 180,
              child: SvgPicture.string(_dmSvg),
            ),
          ),
        ],
      ),
    );
  }
}

class _ActionCircle extends StatelessWidget {
  const _ActionCircle({
    required this.size,
    required this.child,
    required this.tooltip,
    required this.onTap,
  });

  final double size;
  final Widget child;
  final String tooltip;
  final VoidCallback onTap;

  @override
  Widget build(BuildContext context) {
    return Material(
      color: Colors.transparent,
      child: InkWell(
        customBorder: const CircleBorder(),
        onTap: onTap,
        child: Container(
          width: size,
          height: size,
          decoration: BoxDecoration(
            color: Colors.white,
            shape: BoxShape.circle,
            boxShadow: [
              BoxShadow(
                color: Colors.black.withValues(alpha: 0.1),
                blurRadius: 12,
                offset: const Offset(0, 5),
              ),
            ],
          ),
          alignment: Alignment.center,
          child: Tooltip(message: tooltip, child: child),
        ),
      ),
    );
  }
}

